mapper.select:
SELECT t2.* FROM ( SELECT ROWNUM AS rnum , t1.* FROM ( SELECT c1.gallery_category_no -- 갤러리 카테고리 번호 , c1.gallery_category -- 갤러리 카테고리 , c2.galleryno -- 갤러리 번호 , c2.admin_no -- 관리자 번호 , c2.gallery_title -- 갤러리 제목 , to_char(c2.gallery_date,'yyyy-mm-dd') as gallery_date -- 갤러리 작성일 , c2.gallery_hit -- 갤러리 조회수 , c3.fileno -- 첨부파일 , c3.file_url -- 첨부파일 URL FROM gallery_category c1 LEFT JOIN gallery c2 ON ( c2.gallery_category_no = c1.gallery_category_no ) LEFT JOIN files c3 ON ( c3.galleryno = c2.galleryno ) WHERE 1=1 AND c1.gallery_category_no = #{galleryCategoryNo}
ORDER BY c2.galleryno desc
ORDER BY c2.galleryno asc
ORDER BY c2.galleryno desc
) t1 WHERE ROWNUM < = #{endNo} ) t2 WHERE #{startNo} < = rnum ORDER BY rnum

mapper.update:
UPDATE gallery
SET gallery_hit = gallery_hit+1
WHERE galleryno = #{galleryno}

mapper.select:
SELECT c1.gameno
, to_char(c1.game_date, 'yy-mm-dd')game_date
, to_char(c1.game_date, 'mm-dd')game_date
, c1.game_arena, c1.game_status, c1.game_result, c1.game_score, c1.teamno , c2.team_name, c2.team_logo, c1.teamno2, c3.team_name team_name2 , c3.team_logo team_logo2 FROM games c1 LEFT JOIN team c2 ON ( c2.teamno = c1.teamno ) LEFT JOIN team c3 ON ( c3.teamno = c1.teamno2 )
WHERE c1.gameno <= 133 and EXTRACT(MONTH from c1.game_date) = #{select_month}
WHERE c1.gameno >= 134 and EXTRACT(MONTH from c1.game_date) = #{select_month}
WHERE c1.gameno < #{gameno} and EXTRACT(MONTH from c1.game_date) = #{select_month}
ORDER BY c1.gameno asc

SELECT noticeno, notice_title, notice_contents, to_char(notice_date, 'YYYY/MM/DD HH24:MI') notice_date, notice_hit 
FROM notice
WHERE noticeno =#{noticeno}

mapper.update:
UPDATE notice
SET notice_title = #{notice_title}, notice_contents = #{notice_contents}
WHERE notceno = #{noticeno}

UPDATE notice
SET hits = hits+1
WHERE noticeno = #{noticeno}
